import React, {useState} from 'react'
import './App.css';
import { Route } from "wouter"
import Home from "./pages/Home"
import ListOfMovies from './components/ListOfMovies';
import Button from './components/Button';
import { useHandleClickMovie } from './hooks/handleClickMovie';
import { useFilms } from './hooks/useFilms';

export default function App() {
  const [keyword] = useState(['star wars'])
  const {handleClickMovie, films} = useHandleClickMovie()

  return <div id='container' className='bg-zinc-900 h-full text-white flex flex-col items-center'>
     <Route
          component={Home}
          path="/"
      />
    <div className='container'>
        <ListOfMovies films={films} />
    </div>    
    <div>
      <Button handleClick={handleClickMovie}>Más Películas</Button>
    </div>
  </div>
}
