import { useEffect, useState } from 'react'
import getDetails from '../../services/getDetails'

export function useDetail(id) {
    const [parameter, setParameter] = useState([])

    console.log("useDetail: " + id)

    useEffect(() => {
        getDetails({ id })
            .then(elements => {
             
              setParameter(elements)
            }) 
    }, [id]) 


    return {parameter};
}
