import React, { useState } from 'react'
import { useLocation } from 'wouter'
import Menu from '../../components/Menu'
import "./index.css"
import { faSearch } from '@fortawesome/fontawesome-free-solid'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

export default function Home() {
    const [keyword, setKeyword] = useState('')
    const [, pushLocation] = useLocation()

    const handleSubmit = e => {
        e.preventDefault()
        keyword === '' ? pushLocation('/404') 
        : pushLocation(`/search/${keyword}`)
    }

    const handleChange = e => {
        setKeyword(e.target.value)
    }

    return  <div id='container' className='home'>
                <Menu/>
                <div className=' w-screen flex justify-center items-center'>
                    <form onSubmit={handleSubmit}>
                        <input placeholder=" Search a title here..." 
                        onChange={handleChange} 
    type='text' value={keyword} className='rounded-full p-2 w-96 outline-double 
                        focus:outline-8 focus:outline-white font-Montserrat'/>
                        <FontAwesomeIcon icon={faSearch} className="text-black w-10 relative right-10"/>
                    </form>
                </div>
            </div>
}
