import { useEffect, useState } from 'react'
import getMovieDetails from '../../services/getMovieDetails'

export function useMovieDetail(id) {
    const [parameter, setParameter] = useState([])


    useEffect(() => {   
        getMovieDetails({ id })
            .then(elements => {
              setTimeout(setParameter(elements),1000)
            }) 
    }, [id]) 


    return parameter
}
