import React, { useState, useEffect } from 'react'
import './App.css';
import ListOfMovies from './components/ListOfMovies';
import getFilms from './services/getFilms';


export default function App() {
  const [keyword] = useState(['star wars'])
  const [films, setFilms] = useState([])
  const [page, setPages] = useState(1)

  useEffect(() => {
    getFilms({ keyword , page })
        .then(films =>{
          setFilms(films)
        }) 
  }, [keyword, page]) 

  const handleChange = () => {
    console.log("HOLAAAA")
    setPages(page+1)
    getFilms({ keyword , page })
    .then(film =>{
      const movies = films.concat(film)
      setFilms(movies)
    }) 

  }

  return <div id='container' className='bg-zinc-900 h-full text-white flex justify-center'>
    <div className='container'>
      <ListOfMovies films={films} />
      <button onClick={handleChange}>Más Películas</button>
    </div>
  </div>
}
