import React, { useState } from 'react'
import './App.css';
import ListOfMovies from './components/ListOfMovies';
import getFilms from './services/getFilms';
import {useFilms} from "./hooks/useFilms"


export default function App() {
  const [keyword] = useState(['Star wars'])
  const {films, setFil} = useFilms({ keyword })
  const [page, setPages] = useState(1)



  const handleClick = async() => {
    setPages(page+1)
    await getFilms({ keyword , page })
    .then(film => {
      setMovies(movies.contad(film))
    }) 
  }


  return <div id='container' className='bg-zinc-900 h-full text-white flex justify-center'>
    <div className='container'>
      <ListOfMovies films={films} />
      <button onClick={handleClick}>Más Películas</button>
    </div>
  </div>
}
