import getImage from "../getImage"

const api_key = 'b677204d99e9870416faefa56773d56c'


export default async function getBackdrop() { 
    const apiURLMovie = 
    `https://api.themoviedb.org/3/movie/popular?api_key=${api_key}&language=es-ES&page=1`

    const apiURLSerie = 
    `https://api.themoviedb.org/3/tv/popular?api_key=${api_key}&language=es-ES&page=1`



    let apiURLSerieImg = 
    `https://api.themoviedb.org/3/movie/${id}/images?api_key=${api_key}`

    const arr = []

    const arr_id = []

    const arr_logo = []

    fetch(apiURLMovie)
        .then(res => res.json())
        .then(response => {
            const data = response.results
            data.map(movie => { 
                const {backdrop_path ,id } = movie 
                const url = getImage(backdrop_path)
                arr.push(url)
                arr_id.push(id)
                return 1
            }) 
        })
    
        fetch(apiURLSerie)
        .then(res => res.json())
        .then(response => {
            const data = response.results
            data.map(serie => { 
                const {backdrop_path , id } = serie 
                const url = getImage(backdrop_path)
                arr.push(url)
                arr_id.push(id)
                return 1
            }) 
        })

    // shuffle arr 
    arr.sort(() => Math.random() - 0.5);
    
     
    arr_id.map(

        fetch(apiURLMovieImg)
        .then(res => res.json())
        .then(response => {
            const data = response.results
            data.map(serie => { 
                const {backdrop_path , id } = serie 
                const url = getImage(backdrop_path)
                arr.push(url)
                arr_id.push(id)
                return 1
            }) 
        })
    )
    
    return [arr, arr_id, arr_logo]
}
