import getImage from "../getImage"

const api_key = 'b677204d99e9870416faefa56773d56c'


export default async function getBackdrop() { 
    const apiURLMovie = 
    `https://api.themoviedb.org/3/movie/popular?api_key=${api_key}&language=es-ES&page=1`

    const apiURLSerie = 
    `https://api.themoviedb.org/3/tv/popular?api_key=${api_key}&language=es-ES&page=1`

    let apiURLMovieImg = ''

    let apiURLSerieImg = ''
    
    const arr = []

    const arr_idMovie = []

    const arr_idSerie = []

    const arr_logo = []

    fetch(apiURLMovie)
        .then(res => res.json())
        .then(response => {
            const data = response.results
            data.map(movie => { 
                const { backdrop_path, id } = movie 
                const url = getImage(backdrop_path)
                arr.push(url)
                arr_idMovie.push(id)
                return 1
            }) 
        })
    
    fetch(apiURLSerie)
    .then(res => res.json())
    .then(response => {
        const data = response.results
        data.map(serie => { 
            const { backdrop_path , id } = serie 
            const url = getImage(backdrop_path)
            arr.push(url)
            arr_idSerie.push(id)
            return 1
        }) 
    })
     
    arr_idMovie.map(id => {
        apiURLMovieImg = `https://api.themoviedb.org/3/movie/${id}/images?api_key=${api_key}`
        fetch(apiURLMovieImg)
        .then(res => res.json())
        .then(response => {
            const { file_path } = response.logos[0]
            arr_logo.push(getImage(file_path))
            })
        return 1
    })
    console.

    arr_idSerie.map(id => {
        apiURLSerieImg = `https://api.themoviedb.org/3/serie/${id}/images?api_key=${api_key}`
        fetch(apiURLSerieImg)
        .then(res => res.json())
        .then(response => {
            console.table(response)
            const { file_path } = response.logos[0]
            arr_logo.push(getImage(file_path))
            })
        return 1
    })
    
    // console.log(arr_logo)
    return [arr, arr_idMovie.concat(arr_idSerie), arr_logo]
}
