import { useEffect, useState } from 'react'
import getFilms from '../services/getFilms'

export function uses ({ keyword } = { keyword: null }) {
    const [films, setFilms] = useState([])
    const [page] = useState(1)

    useEffect(() => {
        getFilms({ keyword , page })
            .then(films =>{
              setFilms(films)
            }) 
      }, [keyword, page]) 

    return films;
}
