const api_key = 'b677204d99e9870416faefa56773d56c'

export default async function getDetails({ id }) { 
    const apiURL = `https://api.themoviedb.org/3/movie/${id}?api_key=${api_key}&language=es-ES`

    console.log("getDetails: " + id)

    return fetch(apiURL)
        .then(res => res.json())
        .then(response => {
            const data = response
            console.table(data)
            const elements = () => {
                const { backdrop_path, homepage, title, 
                    overview, poster_path, tagline, vote_average,
                    release_data } = data
                const genres = data.genres.map(name => {
                    return {name}
                }) 
                return { backdrop_path, homepage, title, 
                    overview, poster_path, tagline, vote_average,
                    release_data, genres }
            }
            console.log(elements)
            return elements
        })
}
