import React, { useState } from 'react'
import { useLocation } from 'wouter'
import Menu from '../../components/Menu'
import "./index.css"
import { faSearch } from '@fortawesome/fontawesome-free-solid'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import 'tw-elements';
// import { Carousel } from 'react-responsive-carousel'; Crear propio Carousel
import CarouselElements from '../../components/Carousel-Elements'

const list = {
  'HOME': '/',
  'MOVIES': '/movie',
  'SERIES': '/serie',
  'GAMES': '/game'
}

const array = {
  '1': 'https://www.google.com/imgres?imgurl=https%3A%2F%2Fcdn.pixabay.com%2Fphoto%2F2015%2F04%2F23%2F22%2F00%2Ftree-736885__480.jpg&imgrefurl=https%3A%2F%2Fpixabay.com%2Fimages%2Fsearch%2Fsea%2F&tbnid=DH7p1w2o_fIU8M&vet=12ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ..i&docid=QG4MQQA3E95exM&w=771&h=480&q=images&client=firefox-b-d&ved=2ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ', 
  '2': 'https://www.google.com/imgres?imgurl=https%3A%2F%2Fcdn.pixabay.com%2Fphoto%2F2015%2F04%2F23%2F22%2F00%2Ftree-736885__480.jpg&imgrefurl=https%3A%2F%2Fpixabay.com%2Fimages%2Fsearch%2Fsea%2F&tbnid=DH7p1w2o_fIU8M&vet=12ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ..i&docid=QG4MQQA3E95exM&w=771&h=480&q=images&client=firefox-b-d&ved=2ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ',
  '3': 'https://www.google.com/imgres?imgurl=https%3A%2F%2Fcdn.pixabay.com%2Fphoto%2F2015%2F04%2F23%2F22%2F00%2Ftree-736885__480.jpg&imgrefurl=https%3A%2F%2Fpixabay.com%2Fimages%2Fsearch%2Fsea%2F&tbnid=DH7p1w2o_fIU8M&vet=12ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ..i&docid=QG4MQQA3E95exM&w=771&h=480&q=images&client=firefox-b-d&ved=2ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ',
  '4': 'https://www.google.com/imgres?imgurl=https%3A%2F%2Fcdn.pixabay.com%2Fphoto%2F2015%2F04%2F23%2F22%2F00%2Ftree-736885__480.jpg&imgrefurl=https%3A%2F%2Fpixabay.com%2Fimages%2Fsearch%2Fsea%2F&tbnid=DH7p1w2o_fIU8M&vet=12ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ..i&docid=QG4MQQA3E95exM&w=771&h=480&q=images&client=firefox-b-d&ved=2ahUKEwit9OqUucb3AhVQh_0HHeyFAGMQMygAegUIARDXAQ'
}

export default function Home() {
  const [keyword, setKeyword] = useState('')
  const [, pushLocation] = useLocation()
  // const parameter = useTitle()

  const handleSubmit = e => {
    e.preventDefault()
    keyword === '' ? pushLocation('/404')
      : pushLocation(`/search/${keyword}`)
  }

  const handleChange = e => {
    setKeyword(e.target.value)
  }

  return <div id='container' className='home'>
    <Menu list={list} css={"menuV"} cssArticles={"articlesV"} />
    <div className='h-screen sm:w-screen flex flex-col items-center'>

      <Carousel>
          <CarouselElements list={array} />
      </Carousel>

      <form onSubmit={handleSubmit} className="relative">
        <input placeholder=" Search a title here..." onChange={handleChange}
          type='text' value={keyword} className='rounded-full p-3 
                        w-[20rem] sm:w-[23rem] lg:w-[38rem] outline-double 
                        focus:outline-8 focus:outline-white font-Montserrat' />
        <FontAwesomeIcon icon={faSearch} className="text-black text-xl absolute 
                        right-5 top-3 hover:cursor-pointer" onClick={handleSubmit}/>
      </form>
    </div>
  </div>
}
