const api_key = 'b677204d99e9870416faefa56773d56c'

export default async function getDetails({ id }) { 
    const apiURL = `https://api.themoviedb.org/3/movie/${id}?api_key=${api_key}&language=es-ES`


    return fetch(apiURL)
        .then(res => res.json())
        .then(response => {
            const genres = response.genres.map(name => {
                return {name}
            })
            const elements =  [response.backdrop_path, response.homepage, response.title, 
                    response.overview, response.poster_path, response.tagline, response.vote_average,
                    response.release_date, genres]
          
            return elements
        })
}
