import {useState} from "react"
import getAll from "../../services/getAll"
import { useFilms } from "../useFilms"


export function useHandleClickMovie( { keyword }) {
    const {films, setFilms} = useFilms({ keyword })
    const [page, setPages] = useState(2)

    const handleClick = () => {
        setPages(page+1)
        getAll({ keyword , page })
        .then(film => {

            const filter = film.filter(movie => movie.Type === ('movie'))
            const movies = films.concat(filter)
            setFilms(movies)
        })

        setTimeout(() => {
        window.scrollTo({ left: 0, top: document.body.scrollHeight, behavior: "smooth" });
        }, 500)
    }
    
    return {handleClick, films}
}
