import getFilms from '../../services/getFilms';
import Movie from '../Movie'


export default function ListOfMovies() {
  const [films, setFilms] = useState([]);

    useEffect(() => {
        getFilms({ keyword })
            .then(films => setFilms(films)) 
    }, [keyword]) 

  return films.map(({Title, imdbID, Poster}) => 
      <Movie
        key = {imdbID}
        title = {Title}
        image = {Poster}
      /> 
  )
}
