import React, { useState } from 'react'
import { faLongArrowAltLeft } from '@fortawesome/fontawesome-free-solid'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { useLocation } from 'wouter'
import { Carousel } from 'react-responsive-carousel';
import { useMoviePopular } from '../../hooks/useMoviePopular';


export default function Movies() {
    const [, pushLocation] = useLocation()
    const parameter = useMoviePopular()

    const handleClick = () => {
        pushLocation('/')
    }


   


    return <div className='h-screen bg-black border-2 border-green-500 flex flex-col'>

        <section className='w-full h-12 flex items-center pl-6 bg-zinc-900 '>
            <div className='w-10 flex items-center text-zinc-100 hover:cursor-pointer'
                onClick={handleClick}>
                <FontAwesomeIcon icon={faLongArrowAltLeft} className='h-10 mx-2' />
                <p>HOME</p>
            </div>
        </section>

        <div className='w-screen h-6 bg-white border-2 border-red-500'>
            <Carousel className='carousel border-0' autoPlay={setTimeout(true, 1000)}
                infiniteLoop={true} showArrows={true} stopOnHover={false}
                showIndicators={false} showStatus={false} showThumbs={false}
                interval={5000} transitionTime={2000}>

                {parameter.loading === true ?
                    <div className='bg-black flex justify-center items-center'>
                        <div className=''>
                            <img className=''
                                src="https://fbflurry.files.wordpress.com/2020/08/giphy-9.gif"
                                alt='loader' />
                        </div>
                    </div>
                    :
                    parameter.results.map(element => {
                        return <div className='flex justify-center items-center' key={element.id}>
                            <img className='' src={element.url} alt={element.title} />
                        </div>
                    })
                }
            </Carousel>
        </div>

    </div>
}
