import React from 'react'
import { useLocation } from 'wouter'


export default function Home() {
    const [keyword, setKeyword] = useSta('')
    const [path, pushLocation] = useLocation()

    const handleSubmit = e => {
        e.preventDefault()
        pushLocation(`/search/${keyword}`)
        console.log(keyword)
    }

    const handleChange = e => {
        setKeyword(e.target.value)
    }
    return  <div id='container' className='bg-zinc-900 h-screen text-white flex flex-col items-center'>
                <form onSubmit={handleSubmit}>
                    <input placeholder="Search a film here..." onChange={handleChange} type='text' value={keyword} />
                    <input type="submit" />
                </form>
            
            </div>
}
