import {useState} from "react"
import getAll from "../../services/getAll"
import { useAll } from "../useAll"


export function useHandleClick({ keyword, type }) {
    const {parameter, setParameter} = use({ keyword, type })
    const [page, setPages] = useState(2)

    const handleClick = () => {
        setPages(page+1)
        getAll({ keyword , page , type })
        .then(elements => {
            setParameter(parameter.concat(elements))
        })

        setTimeout(() => {
        window.scrollTo({ left: 0, top: document.body.scrollHeight, behavior: "smooth" });
        }, 500)
    }
    
    return {handleClick, parameter}
}
