import React from 'react'
import { useLocation } from 'wouter'

export default function Article({ children , url, cssArticles }) {
  const [, pathLocation] = useLocation()
  
  const handleClick = () => {
    pathLocation(url)
  }
  
  

  return <div key={url} className={
    children === 'LINEA' && cssArticles === 'articlesV' ? 'lineaV' 
  : children === 'LINEA' && cssArticles === 'articlesH' ? 'lineaH'
  : children === 'LOGIN' && cssArticles === 'articlesV' ? 'article translate-y-7'
  : children === 'LOGIN' && cssArticles === 'articlesH' ? 'article'  
  : `article ${cssArticles}`} onClick={handleClick}>
    {children === 'LINEA' ? '' : children}
  </div>
  
}
