import { useEffect, useState } from 'react'
import getMovieCredits from '../../services/getMovieCredits'


export function useMovieCredits(id) {
    const [actors, setActors] = useState([])


    useEffect(() => {   
        getMovieCredits({ id })
            .then(elements => {
              setTimeout( setActors(elements), 1500)
            }) 
    }, [id]) 


    return actors
}
