      {loading: false, results: []}
    )
    const [page] = useState(1)

    useEffect(() => {
        setParameter()
        getAll({ keyword , page })
            .then(elements => {
              setParameter(

              )
              /*
              setTimeout(() => {
                window.scrollTo({ left: 0, top: document.body.scrollHeight, behavior: "smooth" });
              }, 500)
              */
            }) 
    }, [keyword, page]) 


    return parameter.length === [] ? {parameter: [undefined], setParameter}
    : {parameter, setParameter}
}
