import React, { useState } from 'react'
import { useLocation } from 'wouter'
import Menu from '../../components/Menu'
import "./index.css"
import { faSearch } from '@fortawesome/fontawesome-free-solid'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import { useBackdrop } from '../../hooks/useBackdrop'

const list = {
  'HOME': '/',
  'MOVIES': '/movie',
  'SERIES': '/serie',
  'GAMES': '/game'
}

export default function Home() {
  const [keyword, setKeyword] = useState('')
  const [, pushLocation] = useLocation()
  const parameter = useBackdrop()

  const handleSubmit = e => {
    e.preventDefault()
    keyword === '' ? pushLocation('/404')
      : pushLocation(`/search/${keyword}`)
  }

  const handleChange = e => {
    setKeyword(e.target.value)
  }

  console.log(parameter)

  return <div id='container' className='home'>
    <Menu list={list} css={"menuV"} cssArticles={"articlesV"} />
    <div className='h-screen sm:w-screen flex flex-col items-center'>
     
     
     
      <div className='w-11/12 bg-white my-12'>       
            <Carousel className=''
             swipeable={false} 
             autoPlay={true} infiniteLoop={true} showArrows={false} 
             showIndicators={false}  showStatus={false} showThumbs={false} >
{/* 
               {
                 arr.map(element => {  
                   return <div className=''>
                      <img className='h-72'
                      src={element} alt='img1'/>
                    </div>
                 })
                } */}
            </Carousel>
        </div>






      {/*
        <Carousel list={array} cssCarousel={"carousel c-xl"} 
        cssSlides={"slide transition-opacity duration-1000"} 
        cssImages={"slide-image"} />
  
        <Link
            activeClass="active"
            to={`slide${i}`}
            spy={true}
            smooth={true}
            offset={-70}
            duration={500}
         > </Link>
      */}
      
      
      <form onSubmit={handleSubmit} className="relative">
        <input placeholder=" Search a title here..." onChange={handleChange}
          type='text' value={keyword} className='rounded-full p-3 
                        w-[20rem] sm:w-[23rem] lg:w-[38rem] outline-double 
                        focus:outline-8 focus:outline-white font-Montserrat' />
        <FontAwesomeIcon icon={faSearch} className="text-black text-xl absolute 
                        right-5 top-3 hover:cursor-pointer" onClick={handleSubmit}/>
      </form>
    </div>
  </div>
}
