import React, { useState } from 'react'
import './App.css';
import ListOfMovies from './components/ListOfMovies';
import getFilms from './services/getFilms';
import {useFilms} from "./hooks/useFilms"


export default function App() {
  const [keyword] = useState(['star wars'])
  const {films, setFilms} = useFilms({ keyword })
  const [page, setPages] = useState(2)

  const handleClick = () => {
    setPages(page+1)
    getFilms({ keyword , page })
    .then(film => {
      const movies = films.concat(film)
      setFilms(movies)
    }) 
  }


  return <div id='container' className='bg-zinc-900 h-full text-white flex flex-col justify-center'>
    <div className='container'>
      <ListOfMovies films={films} />
    </div>

      <button onClick={handleClick} className='bg-amber-500 text-stone-900 font-bold h-6'>Más Películas</button>

  </div>
}
