import getImage from "../getImage"

const api_key = 'b677204d99e9870416faefa56773d56c'


export default async function getBackdrop() { 
    const apiURLMovie = 
    `https://api.themoviedb.org/3/movie/popular?api_key=${api_key}&language=es-ES&page=1`

    const apiURLSerie = 
    `https://api.themoviedb.org/3/tv/popular?api_key=${api_key}&language=es-ES&page=1`
    
    let apiURLMovieImg = ``

    let apiURLSerieImg = ``

    const arr = []
    const arr2 = []

    const arr_idMovie = []

    const arr_idSerie = []

    const arr_logo = []

    fetch(apiURLMovie)
        .then(res => res.json())
        .then(response => {
            const data = response.results
            data.map(movie => { 
                const { backdrop_path, poster_path } = movie 

                const url = getImage(backdrop_path)
                arr.push(url)

                const img = getImage(poster_path)
                arr2.push(img)

                return 1
            }) 
        })
    
    fetch(apiURLSerie)
        .then(res => res.json())
        .then(response => {
            const data = response.results
            data.map(serie => { 
                const { backdrop_path , poster_path} = serie 

                const url = getImage(backdrop_path)
                arr.push(url)
                
                const img = getImage()


                return 1
            }) 
        })

    /*    
    let i = 0
    function getMovieImg() {
        apiURLMovieImg = `https://api.themoviedb.org/3/movie/${arr_idMovie[i]}/images?api_key=${api_key}`
        fetch(apiURLMovieImg)
        .then(res => res.json())
        .then(response => {
            const data = response
            if (data.success === false) i++
            else if (data.logos[0] === []) arr_logo.push('undefined') 
            else arr_logo.push(getImage(data.logos[0].file_path)); i++
            setTimeout(() => {if (i < arr_idMovie.length) getMovieImg() }, 2000);
            })
    }
    setTimeout(() => {if (i < arr_idMovie.length) getMovieImg() }, 2000);

    let j = 0
    function getSerieImg() {
        apiURLSerieImg = `https://api.themoviedb.org/3/serie/${arr_idSerie[j]}/images?api_key=${api_key}`
        fetch(apiURLSerieImg)
        .then(res => res.json())
        .then(response => {
            const data = response
            if (data.success === false) j++
            else if (data.logos[0] === []) arr_logo.push('undefined') 
            else arr_logo.push(getImage(data.logos[0].file_path)); j++
            setTimeout(() => {if (j < arr_idSerie.length) getSerieImg() }, 2000);
            })
    }
    setTimeout(() => {if (j < arr_idSerie.length) getSerieImg() }, 2000);
    
    */
    return [arr, arr_logo]
}
