import React, {useEffect, useState}  from 'react'
import getFilms from '../../services/getFilms';
import Movie from '../Movie'


export default function ListOfMovies({ keyword , page}) {
  const [films, setFilms] = useState([])

    useEffect(() => {
        getFilms({ keyword , page })
            .then(film =>{
              const movies = film + films
              setFilms(movies)
            }) 
    }, [keyword, page]) 

  return films.map(({Title, imdbID, Poster}) => 
      <Movie
        key = {imdbID}
        title = {Title}
        image = {Poster}
      /> 
  )
}
