import { useEffect, useState } from 'react'
import getSerieDetails from '../../services/getSerieDetails'

export function useSerieDetail(id) {
    const [parameter, setParameter] = useState([])


    useEffect(() => {   
        getSerieDetails({ id })
            .then(elements => {
              setTimeout(setParameter(elements),1000)
            }) 
    }, [id]) 


    return parameter
}
