const maxMovie = 1000
// const maxTV = 11999

const api_key = 'b677204d99e9870416faefa56773d56c'

export default async function getTitle() { 
    let apiURL = ""
    let arr = []

    for (let i = 0; i < maxMovie; i++) {
        apiURL = `https://api.themoviedb.org/3/movie/${i}?api_key=${api_key}&language=es-ES`
        
        fetch(apiURL)
        .then(res => res.json())
        .then(response => {

            const { title } = response
            
            if(title !== undefined){ arr.push(title)}
            
        })
    }
    return arr
}
